import React, { useState } from "react";
import "./Card.css";

const mockData = [
  [{"courseId":4,
  "courseName":"CST451",
  "courseDes":"Senior Capstone",
  "projects":"1","courseSkills":"Documentation","courseImg":"https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png"},{"courseId":5,"courseName":"ITT307","courseDes":"cybersecurity","projects":"4","courseSkills":"security","courseImg":"https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png"},{"courseId":6,"courseName":"CST323","courseDes":"cloud computing","projects":"4","courseSkills":"deploying","courseImg":"https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png"}]
];

function CoursePage() {
  const [courses, setCourses] = useState(mockData);
  const [selectedCourse, setSelectedCourse] = useState(null);

  const handleCourseClick = (course) => {
    setSelectedCourse(course);
  };

  return (
    <div className="c">
    <div className="e-bg"></div>
    <div className="d-bg"></div>

        <a id="Courses"></a>

<div className="titlec" >
    Courses
</div>
<br/>
    <div className="container">
      {selectedCourse ? (
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <div className="card mb-4">
              <img
                className="card-img-top"
                src={selectedCourse.courseImg}
                alt={selectedCourse.courseName}
              />
              <div className="card-body">
                <h5 className="card-title">{selectedCourse.courseName}</h5>
                <p className="card-text">{selectedCourse.courseDes}</p>
                <h6 className="card-subtitle mb-2 text-muted">Skills</h6>
                <ul className="card-text">
                  {selectedCourse.courseSkills.split(",").map((skill) => (
                    <li key={skill}>{skill}</li>
                  ))}
                </ul>
                <button
                  className="btn btn-primary"
                  onClick={() => setSelectedCourse(null)}
                >
                  Back to All Courses
                </button>
              </div>
            </div>
          </div>
          </div>

      ) : (
        <div className="row">
          {courses.map((course) => (
            <div className="col-sm-6 col-md-4" key={course.courseId}>
              <div
                className="card mb-4"
                onClick={() => handleCourseClick(course)}
              >
                <img
                  className="card-img-top"
                  src={course.courseImg}
                  alt={course.courseName}
                />
                <div className="card-body">
                  <h5 className="card-title">{course.courseName}</h5>
                  <p className="card-text">{course.courseDes}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
    </div>
  );
}

export default CoursePage;
