import React, { useState, useEffect } from "react";
import axios from "axios";
function CoursePage() {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    axios
      .get("https://api.example.com/courses")
      .then(function(response) {
        setCourses(response.data);
      })
      .catch(function(error) {
        console.error(error);
      });
  }, []);

  return (
    React.createElement("div", { className: "container" },
      React.createElement("div", { className: "row" },
        courses.map(function(course) {
          return (
            React.createElement("div", { className: "col-sm-6 col-md-4", key: course.id },
              React.createElement("div", { className: "card mb-4" },
                React.createElement("img", { className: "card-img-top", src: course.image, alt: course.name }),
                React.createElement("div", { className: "card-body" },
                  React.createElement("h5", { className: "card-title" }, course.name),
                  React.createElement("p", { className: "card-text" }, course.description),
                  React.createElement("h6", { className: "card-subtitle mb-2 text-muted" }, "Skills"),
                  React.createElement("ul", { className: "card-text" },
                    course.skills.map(function(skill) {
                      return React.createElement("li", { key: skill }, skill);
                    })
                  )
                )
              )
            )
          );
        })
      )
    )
  );
}

ReactDOM.render(React.createElement(CoursePage), document.getElementById("root"));
