import React, { useEffect, useState } from "react";
import "./Card.css";


const mockData = [
  {
    courseId: 4,
    courseName: "CST451",
    courseDes: "Senior Capstone",
    projects: "1",
    courseSkills: "Documentation, Agile, Burndown Chart, Project Management ",
    courseImg:
      "https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png",
  },
  {
    courseId: 5,
    courseName: "ITT307",
    courseDes: "Cybersecurity",
    projects: "4",
    courseSkills: "security",
    courseImg:
      "https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png",
  },
  {
    courseId: 6,
    courseName: "CST323",
    courseDes: "Cloud Computing",
    projects: "4",
    courseSkills: "deploying",
    courseImg:
      "https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png",
  },
];
const MOCK_PROJECTS = [
  {
    projectId: 1,
    projectName: "CAPSTONE",
    projectDes:
      "KATIEDEV is a project to show the students skills while attending GCU.",
    courseId: "4",
    projectSkills: "Cloud Deployment",
    projectFw: "React, Java",
    projectImg: null
  },
  {
    projectId: 2,
    projectName: "testing",
    projectDes: "testing",
    courseId: "4",
    projectSkills: "java",
    projectFw: "top",
    projectImg: "na"
  }
];

const CourseCard = ({ course, onShowProjects }) => {
  const [showProjects, setShowProjects] = useState(false);

  const handleShowProjects = () => {
    setShowProjects(!showProjects);
    onShowProjects(course.courseId);
  };


  return (
    <div className="c">
    <div className="e-bg"></div>
    <div className="d-bg"></div>
    <div className="titlec" >
     Courses
</div>
    <div className="container">
      {selectedCourse ? (
        <div className="row">
          <a id="Courses"></a>
          <div className="col-md-6 offset-md-3">
            <div className="card mb-4">
              <img
                className="card-img-top"
                src={selectedCourse.courseImg}
                alt={selectedCourse.courseName}
              />

              <div className="card-body">
                <h5 className="card-title">{selectedCourse.courseName}</h5>
                <p className="card-text">{selectedCourse.courseDes}</p>
                <h6 className="card-subtitle mb-2 text-muted">Skills</h6>
                <ul className="card-text">
                  {selectedCourse.courseSkills.split(",").map((skill) => (
                    <li key={skill}>{skill}</li>
                  ))}
                </ul>
                <button
                  className="btn btn-primary"
                  onClick={() => setSelectedCourse(null)}
                >
                  Back to All Courses
                </button>
              </div>
              </div>


          </div>
        </div>
      ) : (
        <div className="row">
          {courses.map((course) => (
            <div className="col-sm-6 col-md-4" key={course.courseId}>
              <div
                className="card mb-4"
                onClick={() => handleCourseClick(course)}
              >
                <img
                  className="card-img-top"
                  src={course.courseImg}
                  alt={course.courseName}
                />
                <div className="card-body">
                  <h5 className="card-title">{course.courseName}</h5>
                  <p className="card-text">{course.courseDes}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
    </div>
  );
}

export default Course;
