import React from "react";
import Card from "./Card";

class Course extends React.Component {

    handleSelectOne = (courseId) => {
        console.log("Selected ID: ", courseId)
        this.props.onClick(courseId)
    }

    handleEditAlbum = (courseId) => {
        console.log("The albumID to edit is: ", courseId)
        this.props.onEditAlbum(courseId)
    }


    state = {albumList : [{"courseId":4,"courseName":"CST451","courseDes":"Senior Capstone","projects":"1","courseSkills":"Documentation","courseImg":"https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png"},{"courseId":5,"courseName":"ITT307","courseDes":"cybersecurity","projects":"4","courseSkills":"security","courseImg":"https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png"},{"courseId":6,"courseName":"CST323","courseDes":"cloud computing","projects":"4","courseSkills":"deploying","courseImg":"https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png"}]}


renderList = () => {
   return this.state.albumList.map(
       (course) => {
           return (<Card courseid={course.courseId} courseName={course.courseName} courseDescription={course.courseDes} projects={course.projects} courseSkills={course.courseSkills} courseImg= {course.courseImg}/>)
       }
   )
}

render() {
   return (

       <div className="container">
            <a id="Courses"></a>
           {this.renderList()}
       </div>
   )
}

}

export default Course
