import React from "react";
import "./Contact.css";
import Phone from "../../img/Phone.png";
import Email from "../../img/email.png";
import Address from "../../img/Address.png";
import { useState, useContext } from "react";
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';


import { ThemeContext } from "../../context";
function Contact() {
  const theme = useContext(ThemeContext);
  const darkMode = theme.state.darkMode;

  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const submitHandler = async (e) => {
    e.preventDefault();
    if (!email || !subject || !message) {
      return toast.error('Please fill email, subject and message');
    }
    try {
      setLoading(true);
      const { data } = await axios.post(`/api/email`, {
        email,
        subject,
        message,
      });
      setLoading(false);
      toast.success(data.message);
    } catch (err) {
      setLoading(false);
      toast.error(
        err.response && err.response.data.message
          ? err.response.data.message
          : err.message
      );
    }
  };

  return (
    <div className="c">
        <ToastContainer position="bottom-center" limit={1} />
      <div className="c-bg"></div>
      <div className="c-wrapper">
        <div className="c-left">
        <a id="Contact"></a>
          <h1 className="c-title">Get in Touch</h1> 
          <div className="c-info">
            <div className="c-info-item">
              <img src={Phone} className="c-icon" alt="phone" />
              +1 623-238-2541
            </div>
            <div className="c-info-item">
              <img src={Email} className="c-icon" alt="email" />
              kxtemas01@gmail.com
            </div>
            <div className="c-info-item">
              <img src={Address} className="c-icon" alt="address" />
              Surprize, AZ
            </div>
          </div>
        </div>
        <div className="c-right">
          <p className="c-desc">
            <b>Let's Connect</b>
            <p></p>
            Thank you for visiting my portfolio! If you would like to get in touch with me, please feel free to send an email.
             I would be happy to hear from you and answer any questions you may have. I look forward to connecting with you soon!
          </p>
          <p></p>
          </div>
          </div>

          <div className="App">
      <ToastContainer position="bottom-center" limit={1} />
      <header className="App-header">
        <form onSubmit={submitHandler}>
          <h1>Send Email</h1>
          <div>
            <label htmlFor="email">Email</label>
            <input
              onChange={(e) => setEmail(e.target.value)}
              type="email"
            ></input>
          </div>
          <div>
            <label htmlFor="subject">Subject</label>
            <input
              id="subject"
              type="text"
              onChange={(e) => setSubject(e.target.value)}
            ></input>
          </div>
          <div>
            <label htmlFor="message">Message</label>
            <textarea
              id="message"
              onChange={(e) => setMessage(e.target.value)}
            ></textarea>
          </div>
          <div>
            <label></label>
            <button disabled={loading} type="submit">
              {loading ? 'Sending...' : 'Submit'}
            </button>
          </div>
        </form>

      </header>
      </div>
      </div>


  );
}


export default Contact;
