import React, { useEffect, useState } from "react";
import "./Card.css";


const mockData = [
  {
    courseId: 4,
    courseName: "CST451",
    courseDes: "Senior Capstone",
    projects: "1",
    courseSkills: "Documentation, Agile, Burndown Chart, Project Management ",
    courseImg:
      "https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png",
  },
  {
    courseId: 5,
    courseName: "ITT307",
    courseDes: "Cybersecurity",
    projects: "4",
    courseSkills: "security",
    courseImg:
      "https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png",
  },
  {
    courseId: 6,
    courseName: "CST323",
    courseDes: "Cloud Computing",
    projects: "4",
    courseSkills: "deploying",
    courseImg:
      "https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png",
  },
];
const MOCK_PROJECTS = [
  {
    projectId: 1,
    projectName: "CAPSTONE",
    projectDes:
      "KATIEDEV is a project to show the students skills while attending GCU.",
    courseId: "4",
    projectSkills: "Cloud Deployment",
    projectFw: "React, Java",
    projectImg: null
  },
  {
    projectId: 2,
    projectName: "testing",
    projectDes: "testing",
    courseId: "4",
    projectSkills: "java",
    projectFw: "top",
    projectImg: "na"
  }
];

const App = () => {
  const [courses, setCourses] = useState(mockCourses);
  const [projects, setProjects] = useState([]);

  const showProjects = (courseId) => {
    const courseProjects = mockProjects.filter(project => project.courseId === courseId);
    setProjects(courseProjects);
  }

  return (
    <div className="c">
    <div className="e-bg"></div>
    <div className="d-bg"></div>
    <div className="titlec" >
     Courses
</div>
<div className="container">
      <h1>Courses</h1>
      <div className="card-container">
        {courses.map(course => (
          <div className="card" key={course.courseId}>
            <img src={course.courseImg} alt={course.courseName} />
            <div className="card-body">
              <h2>{course.courseName}</h2>
              <p>{course.courseDes}</p>
              <p><strong>Skills:</strong> {course.courseSkills}</p>
              <button onClick={() => showProjects(course.courseId)}>View Projects</button>
            </div>
          </div>
        ))}
      </div>
      {projects.length > 0 && (
        <div className="project-container">
          <h2>Projects</h2>
          {projects.map(project => (
            <div className="project" key={project.projectId}>
              <img src={project.projectImg} alt={project.projectName} />
              <div className="project-body">
                <h3>{project.projectName}</h3>
                <p>{project.projectDes}</p>
                <p><strong>Skills:</strong> {project.projectSkills}</p>
                <p><strong>Framework:</strong> {project.projectFw}</p>
              </div>
            </div>
          ))}
        </div>

      )}
    </div>
    </div>
  );
};
const mockCourses = [
  {
    courseId: 1,
    courseName: 'CST101',
    courseDes: 'Introduction to Computer Science',
    courseSkills: 'Programming',
    courseImg: 'https://via.placeholder.com/150'
  },
  {
    courseId: 2,
    courseName: 'CST201',
    courseDes: 'Data Structures and Algorithms',
    courseSkills: 'Algorithms',
    courseImg: 'https://via.placeholder.com/150'
  },
  {
    courseId: 3,
    courseName: 'CST301',
    courseDes: 'Database Systems',
    courseSkills: 'SQL, Database Design',
    courseImg: 'https://via.placeholder.com/150'
  }
];
const mockProjects = [
  {
    projectId: 1,
    projectName: 'CAPSTONE',
    projectDes: 'KATIEDEV is a project to show the students skills while attending GCU.',
    courseId: '1',
    projectSkills: 'Cloud Deployment',
    projectFw: 'React, Java',
    projectImg: null
  },
  {
    projectId: 2,
    projectName: 'testing',
    projectDes: 'testing',
    courseId: '1',
    projectSkills: 'java',
    projectFw: 'top',
    projectImg: 'na'
  }
];

ReactDOM.render(<App />, document.getElementById('root'));
