import React, { useState, useEffect } from "react";
import axios from "axios";

function Course() {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    axios
      .get("https://backendkm-env.eba-qakrpxjc.us-east-2.elasticbeanstalk.com/course/")
      .then((response) => setCourses(response.data))
      .catch((error) => console.error(error));
  }, []);

  return (
    <div>
         <a id="Projectinfo"></a>
      {courses.map((course) => (
        <div key={course.courseId}>
          <h2>{course.courseName}</h2>
          <img src={course.courseImg} alt={course.courseName} />
          <p>{course.courseDes}</p>
          <h3>Skills</h3>
          <ul>
            {course.courseSkills.map((courseSkill) => (
              <li key={courseSkill}>{courseSkill}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}
export default Course;
