import React, { useEffect, useState } from "react";
import "./Card.css";


const mockData = [
  {
    courseId: 4,
    courseName: "CST451",
    courseDes: "Senior Capstone",
    projects: "1",
    courseSkills: "Documentation, Agile, Burndown Chart, Project Management ",
    courseImg:
      "https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png",
  },
  {
    courseId: 5,
    courseName: "ITT307",
    courseDes: "Cybersecurity",
    projects: "4",
    courseSkills: "security",
    courseImg:
      "https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png",
  },
  {
    courseId: 6,
    courseName: "CST323",
    courseDes: "Cloud Computing",
    projects: "4",
    courseSkills: "deploying",
    courseImg:
      "https://miro.medium.com/max/1400/1*UvWOnSlWP7AbbOtSVUeHMg.png",
  },
];
const MOCK_PROJECTS = [
  {
    projectId: 1,
    projectName: "CAPSTONE",
    projectDes:
      "KATIEDEV is a project to show the students skills while attending GCU.",
    courseId: "4",
    projectSkills: "Cloud Deployment",
    projectFw: "React, Java",
    projectImg: null
  },
  {
    projectId: 2,
    projectName: "testing",
    projectDes: "testing",
    courseId: "4",
    projectSkills: "java",
    projectFw: "top",
    projectImg: "na"
  }
];

const CourseCard = ({ course, onShowProjects }) => {
  const [showProjects, setShowProjects] = useState(false);

  const handleShowProjects = () => {
    setShowProjects(!showProjects);
    onShowProjects(course.courseId);
  };
  return (
    <div className="c">
    <div className="e-bg"></div>
    <div className="d-bg"></div>
    <div className="titlec" >
     Courses
</div>
    <div className="card">
      <img src={course.courseImg} alt={course.courseName} />
      <h1>{course.courseName}</h1>
      <p className="title">{course.courseDes}</p>
      <p>{course.courseSkills}</p>
      {showProjects && (
        <div className="project-container">
          <h2>Projects:</h2>
          {MOCK_PROJECTS.filter(
            (project) => project.courseId === course.courseId
          ).map((project) => (
            <div className="project-card" key={project.projectId}>
              <h3>{project.projectName}</h3>
              <p>{project.projectDes}</p>
              <p>{project.projectSkills}</p>
              <p>{project.projectFw}</p>
              {project.projectImg && (
                <img src={project.projectImg} alt={project.projectName} />
              )}
            </div>
          ))}
        </div>
      )}
      <button onClick={handleShowProjects}>
        {showProjects ? "Hide Projects" : "Show Projects"}
      </button>
    </div>
    </div>
  );
};
const mockCourses = [
  {
    courseId: 1,
    courseName: 'CST101',
    courseDes: 'Introduction to Computer Science',
    courseSkills: 'Programming',
    courseImg: 'https://via.placeholder.com/150'
  },
  {
    courseId: 2,
    courseName: 'CST201',
    courseDes: 'Data Structures and Algorithms',
    courseSkills: 'Algorithms',
    courseImg: 'https://via.placeholder.com/150'
  },
  {
    courseId: 3,
    courseName: 'CST301',
    courseDes: 'Database Systems',
    courseSkills: 'SQL, Database Design',
    courseImg: 'https://via.placeholder.com/150'
  }
];
const mockProjects = [
  {
    projectId: 1,
    projectName: 'CAPSTONE',
    projectDes: 'KATIEDEV is a project to show the students skills while attending GCU.',
    courseId: '1',
    projectSkills: 'Cloud Deployment',
    projectFw: 'React, Java',
    projectImg: null
  },
  {
    projectId: 2,
    projectName: 'testing',
    projectDes: 'testing',
    courseId: '1',
    projectSkills: 'java',
    projectFw: 'top',
    projectImg: 'na'
  }
];

ReactDOM.render(<App />, document.getElementById('root'));
