import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Course.html";

function Course() {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    axios
      .get("https://backendkm-env.eba-qakrpxjc.us-east-2.elasticbeanstalk.com/course/")
      .then((response) => setCourses(response.data))
      .catch((error) => console.error(error));
  }, []);

  return (
    <div className="container">
          <a id="Courses"></a>
    <div className="row">
      {courses.map((course) => (
        <div className="col-sm-6 col-md-4" key={course.courseId}>
          <div className="card mb-4">
            <img
              className="card-img-top"
              src={course.courseImg}
              alt={course.courseName}
            />
            <div className="card-body">
              <h5 className="card-title">{course.courseName}</h5>
              <p className="card-text">{course.courseDes}</p>
              <h6 className="card-subtitle mb-2 text-muted">Skills</h6>
              <ul className="card-text">
                {course.courseSkills.map((skill) => (
                  <li key={skill}>{skill}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      ))}
    </div>
  </div>
);
                }

export default Course;
