import React from "react";
 import Card from "./Card";
 import './App.js'
 import './App.css'

 class App extends React.Component {

     state = {albumList : [
             {
                 "id": 0,
                 "title": "Folklore",
                 "description": "Taylor swifts cabin era",
                 "year": 2020,
                 "image": "https://picsum.photos/100/100"

             },
             {
                 "id": 1,
                 "title": "Evermore",
                 "description": "Only the greatest tool album ever.",
                 "year": 2021,
                 "image": "https://picsum.photos/100/100"

             },
             {
                 "id": 2,
                 "title": "Midnights",
                 "description": "The newest album.",
                 "year": 2022,
                 "image": "https://picsum.photos/100/100"

             }
         ]};

     renderList = () => {
         return this.state.albumList.map(
             (album) => {
                 return (<Card albumTitle={album.title} albumDescription={album.description} buttonText="OK" imageUrl={album.image}/>);
             }
         );
     };

     render() {
         return (
             <div className="container">
                 {this.renderList()}
             </div>
         );
     }
 }

 export default App
